nta.declaration:
// Place global declarations here.

const int empty = 0;
const int black = 1;
const int white = 2;

typedef int[0,5] tokens;
int board[7] = {black, black, black, empty, white, white, white};
int position[6] = {0, 1, 2, 4, 5, 6};

chan move_right_one[tokens];
chan move_right_two[tokens];
chan move_left[tokens];

void move_right_one(tokens t) {
    int pos = position[t];
    board[pos+1] = black;
    board[pos] = empty;
    position[t] = pos+1;
}

void move_right_two(tokens t) {
    int pos = position[t];
    board[pos+2] = black;
    board[pos] = empty;
    position[t] = pos+2;
}

void move_right_action(tokens t) {
    int pos = position[t];
    if (pos+1 < 7 && board[pos+1] == empty) {
        board[pos+1] = black;
        board[pos] = empty;
        position[t] = pos+1;
    } else if (pos+2 < 7 && board[pos+2] == empty) {
        board[pos+2] = black;
        board[pos] = empty;
        position[t] = pos+2;
    }
}

void move_left_action(tokens t) {
    int pos = position[t];
    if (pos-1 >= 0 && board[pos-1] == empty) {
        board[pos-1] = white;
        board[pos] = empty;
        position[t] = pos-1;
    } else if (pos-2 >= 0 && board[pos-2] == empty) {
        board[pos-2] = white;
        board[pos] = empty;
        position[t] = pos-2;
    }
}
